import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function CampeonatoDetailsPage() {
    const { id } = useParams(); // pega o id da URL
    const [fases, setFases] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        async function fetchDetails() {
            setLoading(true);
            setError('');
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:8080/api/v1/campeonatos/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setFases(response.data);
            } catch (err) {
                setError('Erro ao carregar detalhes do campeonato.');
            } finally {
                setLoading(false);
            }
        }

        fetchDetails();
    }, [id]);

    if (loading) return <p>Carregando detalhes...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div>
            <h2>Detalhes do Campeonato</h2>
            {fases.length === 0 && <p>Nenhuma fase encontrada.</p>}
            {fases.map((fase, idx) => (
                <div key={idx} style={{ marginBottom: '2rem' }}>
                    <h3>{fase.name}</h3>
                    <h4>Partidas:</h4>
                    <ul>
                        {fase.partidas.map((partida) => (
                            <li key={partida.uuid}>
                                {partida.teamA.name} vs {partida.teamB.name} -{' '}
                                Vencedor: {partida.winner ? partida.winner.name : 'Ainda não decidido'}
                            </li>
                        ))}
                    </ul>
                    <h4>Vencedores:</h4>
                    <ul>
                        {fase.vencedores.length === 0 ? (
                            <li>Nenhum vencedor ainda</li>
                        ) : (
                            fase.vencedores.map((team) => <li key={team.id}>{team.name}</li>)
                        )}
                    </ul>
                </div>
            ))}
        </div>
    );
}
