import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function DetalhesCampeonatoPage({ campeonatoId }) {
    const [fases, setFases] = useState([]);
    const [times, setTimes] = useState([]);
    const [error, setError] = useState('');
    const [selectedWinners, setSelectedWinners] = useState({}); // { partidaId: timeId }

    useEffect(() => {
        const fetchDetalhes = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:8080/api/v1/campeonatos/${campeonatoId}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFases(response.data);
                // Supondo que os times possam ser extraídos das fases, ou faça uma outra requisição para os times do campeonato
            } catch (err) {
                setError('Erro ao carregar detalhes do campeonato');
            }
        };
        fetchDetalhes();
    }, [campeonatoId]);

    const handleWinnerChange = (partidaId, timeId) => {
        setSelectedWinners(prev => ({ ...prev, [partidaId]: timeId }));
    };

    const registrarResultado = async (partidaId) => {
        try {
            const token = localStorage.getItem('token');
            const timeId = selectedWinners[partidaId];
            if (!timeId) {
                alert('Selecione um time vencedor');
                return;
            }
            await axios.patch(
                `http://localhost:8080/api/v1/campeonatos/${campeonatoId}/resultado/partida/${partidaId}`,
                { id: timeId, name: '' }, // o backend só usa o id do time, o nome pode ficar vazio
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert('Resultado registrado com sucesso');
            // Atualize os dados ou faça um refetch do campeonato/fases para atualizar UI
        } catch (err) {
            alert('Erro ao registrar resultado');
        }
    };

    return (
        <div style={{ maxWidth: '600px', margin: 'auto' }}>
            <h2>Detalhes do Campeonato</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {fases.map(fase => (
                <div key={fase.id}>
                    <h3>{fase.name}</h3>
                    <ul>
                        {fase.partidas.map(partida => (
                            <li key={partida.uuid}>
                                {partida.teamA.name} vs {partida.teamB.name} -
                                {partida.winner ? (
                                    <span style={{ color: 'green' }}> Vencedor: {partida.winner.name}</span>
                                ) : (
                                    <>
                                        <select onChange={e => handleWinnerChange(partida.uuid, e.target.value)} defaultValue="">
                                            <option value="" disabled>Selecione vencedor</option>
                                            {/* Aqui você pode popular os options com os times do campeonato */}
                                            <option value={partida.teamA.id}>{partida.teamA.name}</option>
                                            <option value={partida.teamB.id}>{partida.teamB.name}</option>
                                        </select>
                                        <button onClick={() => registrarResultado(partida.uuid)}>Registrar resultado</button>
                                    </>
                                )}
                            </li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    );
}
