// src/pages/LoginPage.js
import React, { useState } from 'react';
import api from '../api/axios';

export default function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setIsRegistering] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let result;
      if (isRegistering) {
  result = await api.post('/register', { email, password }); // se register aceita email
  alert('Usuário registrado! Agora faça o login.');
  setIsRegistering(false);
} else {
  result = await api.post('/authenticate', { username: email, password }); // aqui username
  localStorage.setItem('token', result.data.token);
  alert('Login realizado com sucesso!');
}

    } catch (err) {
      console.error(err);
      setError('Erro ao realizar operação. Verifique as credenciais.');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: 'auto' }}>
      <h2>{isRegistering ? 'Registrar' : 'Login'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="E-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br />
        <button type="submit">
          {isRegistering ? 'Registrar' : 'Entrar'}
        </button>
      </form>
      <p>
        {isRegistering ? 'Já tem uma conta?' : 'Não tem uma conta?'}{' '}
        <button onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? 'Login' : 'Registrar'}
        </button>
      </p>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
