import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function DetalhesCampeonatoPage() {
    const { id } = useParams();
    const [fases, setFases] = useState([]);
    const [erro, setErro] = useState('');
    const token = localStorage.getItem('token');

    useEffect(() => {
        async function carregarDetalhes() {
            try {
                const response = await axios.get(`http://localhost:8080/api/v1/campeonatos/${id}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFases(response.data);
            } catch (err) {
                setErro('Erro ao carregar detalhes do campeonato.');
            }
        }
        carregarDetalhes();
    }, [id, token]);

    return (
        <div style={{ maxWidth: '800px', margin: 'auto' }}>
            <h2>Detalhes do Campeonato</h2>
            {erro && <p style={{ color: 'red' }}>{erro}</p>}
            {fases.length === 0 ? (
                <p>Nenhuma fase encontrada.</p>
            ) : (
                fases.map((fase, index) => (
                    <div key={index} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
                        <h3>{fase.name}</h3>
                        <h4>Partidas:</h4>
                        {fase.partidas.length === 0 ? (
                            <p>Sem partidas nessa fase.</p>
                        ) : (
                            <ul>
                                {fase.partidas.map((partida) => (
                                    <li key={partida.uuid}>
                                        {partida.teamA.name} vs {partida.teamB.name} -{' '}
                                        <strong>Vencedor: {partida.winner ? partida.winner.name : 'Não definido'}</strong>
                                    </li>
                                ))}
                            </ul>
                        )}
                        <h4>Vencedores da fase:</h4>
                        {fase.vencedores.length === 0 ? (
                            <p>Sem vencedores definidos.</p>
                        ) : (
                            <ul>
                                {fase.vencedores.map((team) => (
                                    <li key={team.id}>{team.name}</li>
                                ))}
                            </ul>
                        )}
                    </div>
                ))
            )}
        </div>
    );
}
