import React, { useState } from 'react';
import { register, authenticate } from '../api/auth';

export default function LoginPage() {
  const [name, setName] = useState('');
  const [lastname, setLastname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setIsRegistering] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isRegistering) {
        // Enviar name, lastname, email e password para registro
        await register({ name, lastname, email, password });
        alert('Usuário registrado! Agora faça o login.');
        setIsRegistering(false);
        // Limpar campos
        setName('');
        setLastname('');
        setEmail('');
        setPassword('');
      } else {
        const result = await authenticate({ email, password });
        localStorage.setItem('token', result.token);
        alert('Login realizado com sucesso!');
        // redirecionar para outra página se quiser
      }
      setError('');
    } catch (err) {
      setError('Erro ao realizar operação. Verifique as credenciais.');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: 'auto' }}>
      <h2>{isRegistering ? 'Registrar' : 'Login'}</h2>
      <form onSubmit={handleSubmit}>
        {isRegistering && (
          <>
            <input
              type="text"
              placeholder="Nome"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            /><br />
            <input
              type="text"
              placeholder="Sobrenome"
              value={lastname}
              onChange={(e) => setLastname(e.target.value)}
              required
            /><br />
          </>
        )}
        <input
          type="email"
          placeholder="E-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br />
        <input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        /><br />
        <button type="submit">
          {isRegistering ? 'Registrar' : 'Entrar'}
        </button>
      </form>
      <p>
        {isRegistering ? 'Já tem uma conta?' : 'Não tem uma conta?'}{' '}
        <button onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? 'Login' : 'Registrar'}
        </button>
      </p>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
