import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function MainPage() {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    return (
        <div style={{ maxWidth: '500px', margin: 'auto' }}>
            <h2>Menu Principal</h2>
            <button onClick={() => navigate('/criar-campeonato')}>Criar Campeonato</button><br /><br />
            <button onClick={() => navigate('/campeonatos')}>Buscar Campeonatos</button><br /><br />
            <button onClick={() => navigate('/detalhes')}>Ver Detalhes de Campeonato</button><br /><br />
            <button onClick={() => navigate('/deletar')}>Deletar Campeonato</button><br /><br />
            <button onClick={() => navigate('/registrar-resultado')}>Registrar Resultado de Partida</button><br /><br />
            <button onClick={handleLogout}>Sair</button>
        </div>
    );
}
