import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function DetalhesCampeonatoPage() {
    const { id } = useParams(); // pega o id da URL
    const [fases, setFases] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchFases = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Usuário não autenticado');
                    setLoading(false);
                    return;
                }
                const response = await axios.get(`http://localhost:8080/api/v1/campeonatos/${id}`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFases(response.data);
            } catch (err) {
                setError('Erro ao buscar detalhes do campeonato');
            } finally {
                setLoading(false);
            }
        };

        fetchFases();
    }, [id]);

    if (loading) return <p>Carregando...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;
    if (fases.length === 0) return <p>Este campeonato não possui fases registradas.</p>;

    return (
        <div style={{ maxWidth: '800px', margin: 'auto' }}>
            <h2>Detalhes do Campeonato</h2>
            {fases.map((fase, index) => (
                <div key={fase.id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
                    <h3>Fase {index + 1}: {fase.name}</h3>
                    <ul>
                        {fase.matches.map(match => (
                            <li key={match.id}>
                                {match.teamA.name} vs {match.teamB.name} - Resultado: {match.result ? match.result : 'Ainda não registrado'}
                            </li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    );
}
